</template>

<script>
import $ from "jquery"
import axios from "@/axios"
import { Modal } from "bootstrap"
import { ref } from "vue"

export default {
    setup() {
        const date = ref(new Date());
        const format = (date) => {
            const d = date.getDate();
            const m = date.getMonth() + 1;
            const y = date.getFullYear();
            return `${d}/${m}/${y}`;
        }
        return {
            date, format
        }
    },
    components: {

    },
    data() {
        return {
            cust_name: "",
            cust_addr: "",
            cust_tel: "",
            check_bill_header: [],
            customer_data: [],
            rubber_size: [],
            rubber_brand: [],
            import_header: {
                import_id: "",
                import_book_id: "",
                import_book_no: "",
                import_user: "",
                cust_auto_id: "",
                import_type_id: "",
                import_date: "",
                import_status: "",
            },
            import_detail_list: [],
            import_detail: {
                import_id: "",
                rub_size_id: "",
                rub_brand_id: "",
                manufac_id: "",
                import_detail_status: "",
            },
        }
    },
    mounted() {
        let import_book_id = sessionStorage.getItem("import_book_id");
        let import_book_no = sessionStorage.getItem("import_book_no");
        if (import_book_id != null && import_book_no != null) {
            this.chkImportHeader(import_book_id, import_book_no);
        } else {
            let import_id = sessionStorage.getItem("import_id");
            this.import_detail.import_id = import_id;
            this.chkImportHeaderById(import_id);
            this.getImportDetail(import_id);
        }
    },
    beforeUnmount() {
        sessionStorage.clear();
    },
    methods: {
        showDatePicker() {
            let data = this.import_header;
            $(document).ready(() => {
                $('#datepicker').datepicker({
                    format: "dd/mm/yyyy",
                    autoclose: true,
                    toggleActive: true,
                });
                $('#datepicker').change(() => {
                    data.import_date = $('#datepicker').val();
                })
            });
        },
        convertDateToMySql(isdate) {
            const convertdate = isdate.split("/");
            let dateToMySql = convertdate[2] + "-" + convertdate[1] + "-" + convertdate[0];
            return dateToMySql;
        },
        onSelect2_Size() {
            $(document).ready(() => {
                let rec = this.import_detail;
                $('#findSize').select2({
                    theme: "bootstrap-5",
                    closeOnSelect: true,
                    width: '100%',
                    dropdownParent: $("#findSize").parent(),
                    placeholder: "--- เลือกขนาดยาง ---",
                });
                $('#findSize').on('select2:select', (e) => {
                    let data = e.params.data;
                    rec.rub_size_id = data.id;
                })
            });
        },
        clearSelect2() {
            $(document).ready(() => {
                $('#findSize').val("");
            })
        },
        getUseData() {
            this.getRubberSize();
            this.getRubberBrand();
        },
        async chkImportHeader(val1, val2) {
            await axios.get(`billimport/search/${val1}/${val2}`).then((response) => {
                // this.check_bill_header = response.data;
                this.import_header.import_id = response.data[0].import_id;
                this.import_header.import_book_id = response.data[0].import_book_id;
                this.import_header.import_book_no = response.data[0].import_book_no;
                this.import_header.import_user = response.data[0].import_user;
                this.import_header.cust_auto_id = response.data[0].cust_auto_id;
                this.import_header.import_type_id = response.data[0].import_type_id;
                this.import_header.import_date = response.data[0].new_import_date;
                this.import_header.import_status = response.data[0].import_status;
            });
        },
        async chkImportHeaderById(val) {
            await axios.get(`billimport/searchid/${val}`).then((response) => {
                // this.check_bill_header = response.data;
                this.import_header.import_id = response.data[0].import_id;
                this.import_header.import_book_id = response.data[0].import_book_id;
                this.import_header.import_book_no = response.data[0].import_book_no;
                this.import_header.import_user = response.data[0].import_user;
                this.import_header.cust_auto_id = response.data[0].cust_auto_id;
                this.import_header.import_type_id = response.data[0].import_type_id;
                this.import_header.import_date = response.data[0].new_import_date;
                this.dd = this.import_header.import_date.substring(0, 2);
                this.mm = this.import_header.import_date.substring(3, 5);
                this.yy = this.import_header.import_date.substring(6, 10);
                this.import_header.import_status = response.data[0].import_status;
                this.cust_name = response.data[0].customerName;
                this.cust_addr = response.data[0].customerAddr;
                this.cust_tel = response.data[0].customerTel;
            });
        },
        newImportDetail() {
            if ((this.import_header.import_book_id == null) ||
                (this.import_header.import_book_no == null) ||
                (this.import_header.import_date == null)) {
                alert('ข้อมูลหลักไม่ครบถ้วน.,กรุณาตรวจสอบ !!!')
                // document.getElementById("book_id").value = 'xxx';
                document.getElementById("book_id").focus();
                document.getElementById("book_id").select();
                //document.getElementById("book_no").disabled = true;             
            } else {
                this.onSelect2_Size();
                this.getUseData();
                this.showModal('newImportDetail');
            }
        },
        saveImportDetail() {
            if (confirm("ยืนยันการบันทึกรายการ") == true) {
                axios.post(`billimport/detail/new`, this.import_detail).then(() => {
                    this.import_detail = {
                        import_id: "",
                        rub_size_id: "",
                        rub_brand_id: "",
                        manufac_id: "",
                        import_detail_status: ""
                    };
                }).catch((error) => {
                    console.log(error);
                });
                this.hideModal("newImportDetail");
                // alert(this.import_header.import_id);
                $("#showimportdetail").DataTable().destroy();
                this.getImportDetail(this.import_header.import_id);
            }
        },
        getImportDetail(val) {
            axios.get(`billimport/detail/read/${val}`).then((response) => {
                this.import_detail_list = response.data;
                $(document).ready(function () {
                    $("#showimportdetail").DataTable({
                        "scrollY": '350px',
                        "scorllX": false,
                        "bJQueryUI": true,
                        "sPaginationType": "full_numbers",
                        "bFilter": false,
                        "paging": false,
                        "scrollCollapse": true,
                        "rowReorder": true,
                        language: {
                            decimal: "",
                            emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
                            info: "พบข้อมูล _TOTAL_ เรคอร์ด",
                            // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
                            infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
                            infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
                            infoPostFix: "",
                            thousands: ",",
                            lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                            loadingRecords: "Loading...",
                            processing: "",
                            search: "ค้นหาข้อมูล:",
                            zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                            paginate: {
                                first: "หน้าแรก",
                                last: "หน้าสุดท้าย",
                                next: "ต่อไป",
                                previous: "ย้อนกลับ",
                            },
                            aria: {
                                sortAscending: ": activate to sort column ascending",
                                sortDescending: ": activate to sort column descending",
                            },
                        },
                    });
                });

                // this.setDatatables();
            });
        },
        setDatatables() {
            $(document).ready(function () {
                $("#showimportdetail").DataTable({
                    "scrollY": '250px',
                    bJQueryUI: true,
                    sPaginationType: "full_numbers",
                    language: {
                        decimal: "",
                        emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
                        info: "พบข้อมูล _TOTAL_ เรคอร์ด",
                        // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
                        infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
                        infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
                        infoPostFix: "",
                        thousands: ",",
                        lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                        loadingRecords: "Loading...",
                        processing: "",
                        search: "ค้นหาข้อมูล:",
                        zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                        paginate: {
                            first: "หน้าแรก",
                            last: "หน้าสุดท้าย",
                            next: "ต่อไป",
                            previous: "ย้อนกลับ",
                        },
                        aria: {
                            sortAscending: ": activate to sort column ascending",
                            sortDescending: ": activate to sort column descending",
                        },
                    },
                });
            });
        },
        getCustomer(val) {
            axios.get(`customer/findbyautoid/${val}`).then((response) => {
                // this.customer_data = response.data;
                // console.log(response.data[0].cust_auto_id);
                this.import_header = {
                    cust_auto_id: response.data[0].cust_auto_id
                }
                if (response.data[0].cust_auto_id == 1) { this.import_header.import_type_id = 2 }
                else this.import_header.import_type_id = 1

                this.cust_name = response.data[0].customerName
                this.cust_addr = response.data[0].customerAddr
                this.cust_tel = response.data[0].customerTel
            });
        },
        getRubberSize() {
            axios.get(`rubbersize/read`).then((response) => {
                this.rubber_size = response.data;
            });
        },
        getRubberBrand() {
            axios.get(`rubberbrand/read`).then((response) => {
                this.rubber_brand = response.data;
            });
        },
        async showModal(e) {
            let myModal = await new Modal(document.getElementById(e))
            await myModal.show()
        },
        async hideModal(e) {
            const body = document.querySelector("body");
            await $(".modal-backdrop").remove();
            await $(`#${e}`).removeClass("in");
            await $(`#${e}`).hide();
            body.style.overflow = "auto";
        },
    },
}
</script>

