import "core-js/modules/es.array.push.js";
import axios from "@/axios";
import $ from "jquery";
import { Modal } from "bootstrap";
import { ref } from "vue";
export default {
  setup() {
    const date = ref(new Date());

    const format = date => {
      const d = date.getDate();
      const m = date.getMonth() + 1;
      const y = date.getFullYear();
      return `${d}/${m}/${y}`;
    };

    return {
      date,
      format
    };
  },

  data() {
    return {
      isLoading: false,
      data_row: [],
      province_id: [],
      newRec: {
        customerId: "",
        customerName: "",
        customerAddr: "",
        province_id: "",
        postCode: "",
        customerTel: "",
        contactName: "",
        contactTel: "",
        contactPosition: "",
        payType: "",
        creditDay: "",
        creditLimit: "",
        priceType: "",
        remark: "",
        customerDel: ""
      },
      editCustomer: {
        customerId: "",
        customerName: "",
        customerAddr: "",
        province_id: "",
        postCode: "",
        customerTel: "",
        contactName: "",
        contactTel: "",
        contactPosition: "",
        payType: "",
        creditDay: "",
        creditLimit: "",
        priceType: "",
        remark: "",
        customerDel: ""
      },
      // temp
      tmp_cust_name: "",
      tmp_cust_addr: "",
      tmp_cust_tel: "",
      import_header: {
        import_book_id: "",
        import_book_no: "",
        cust_auto_id: "",
        import_user: 1,
        import_type_id: "",
        import_date: "",
        import_status: "รอดำเนินการ"
      }
    };
  },

  created() {
    this.getData();
  },

  computed: {
    dateTime() {
      const dt_format = new Date();
      return dt_format.getDate() + "/" + dt_format.getMonth() + 1 + "/" + dt_format.getFullYear();
    }

  },
  methods: {
    onSelect2() {
      $(document).ready(() => {
        let rec = this.editCustomer;
        $("#findSelect").select2({
          theme: "bootstrap-5",
          closeOnSelect: true,
          width: "100%",
          dropdownParent: $("#findSelect").parent(),
          placeholder: "--- เลือกจังหวัด ---" // tags: true,

        });
        $("#findSelect").on("select2:select", function (e) {
          let data = e.params.data; // $('#findSize1').val(data.id)

          rec.province_id = data.id;
        });
      });
    },

    goLoading() {
      this.isLoading = true;
      setTimeout(() => {
        this.isLoading = false;
      }, 5000);
    },

    inputFocus(Modal, InputModal) {
      const myModal = document.getElementById(Modal);
      const myInput = document.getElementById(InputModal);
      myModal.addEventListener("shown.bs.modal", () => {
        myInput.focus();
      });
    },

    newCustomer() {
      this.$router.push("/newcustomer");
    },

    editCustomer_Modal(val) {
      this.editCustomer.customerId = val.customerId;
      this.editCustomer.customerName = val.customerName;
      this.editCustomer.customerAddr = val.customerAddr;
      this.editCustomer.province_id = val.province_id;
      this.editCustomer.postCode = val.postCode;
      this.editCustomer.customerTel = val.customerTel;
      this.editCustomer.contactName = val.contactName;
      this.editCustomer.contactTel = val.contactTel;
      this.editCustomer.contactPosition = val.contactPosition;
      this.editCustomer.payType = val.payType;
      this.editCustomer.creditDay = val.creditDay;
      this.editCustomer.creditLimit = val.creditLimit;
      this.editCustomer.priceType = val.priceType;
      this.editCustomer.remark = val.remark;
      this.editCustomer.customerDel = val.customerDel;
      this.showModal("editCustModal");
      this.onSelect2(); //this.inputFocus("editCustModal", "input_name");
    },

    async saveNewrec() {
      // await axios.post(`${apiURL}/customer/new`, this.newRec).then(() => {
      await axios.post(`customer/new`, this.newRec).then(() => {
        this.newRec = {
          customerId: "",
          customerName: "",
          customerAddr: "",
          province_id: "",
          postCode: "",
          customerTel: "",
          contactName: "",
          contactTel: "",
          contactPosition: "",
          payType: "",
          creditDay: "",
          creditLimit: "",
          priceType: "",
          remark: "",
          customerDel: ""
        };
        this.$router.push("/showcustomer");
        console.log("Process success!");
      }).catch(error => {
        console.log(error);
      });
      this.$router.push("/");
    },

    async saveEditCustomer() {
      //let editModal = document.getElementById('editCustModal')
      if (confirm("ยืนยันการเปลี่ยนแปลงข้อมูล ?") == true) {
        await axios.post(`customer/update`, this.editCustomer).then(() => {
          this.editCustomer = {
            customerId: "",
            customerName: "",
            customerAddr: "",
            province_id: "",
            postCode: "",
            customerTel: "",
            contactName: "",
            contactTel: "",
            contactPosition: "",
            payType: "",
            creditDay: "",
            creditLimit: "",
            priceType: "",
            remark: "",
            customerDel: ""
          };
          this.hideModal("editCustModal"); // ---------------------------------

          $("#showcustomer").DataTable().destroy(); //--- ทำลาย Datatable

          this.getData();
          console.log("Process success!");
        }).catch(error => {
          console.log(error);
        });
      }
    },

    async getData() {
      this.goLoading();
      await axios.get(`customer/read`).then(response => {
        this.data_row = response.data;
        $(document).ready(function () {
          $("#showcustomer").DataTable({
            // "fnRowCallback": function (nRow, aData,) { // เช็คเงื่อนไขในแต่ละเรคคอร์ด
            //      if (aData[3] == "handsome") {
            //           $('td', nRow).css('color', 'Red');
            //      }
            // }
            //"dom": 'Bfrtip',
            //"buttons": ['excel', 'pdf'],
            scrollY: "300px",
            // "scrollX": false,
            // scrollCollapse: true,
            // paging: false,
            // rowReorder: true,
            // "bJQueryUI": true,
            autoFill: true,
            sPaginationType: "full_numbers",
            language: {
              decimal: "",
              emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
              info: "พบข้อมูล _TOTAL_ เรคอร์ด",
              // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
              infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
              infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
              infoPostFix: "",
              thousands: ",",
              lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
              loadingRecords: "Loading...",
              processing: "",
              search: "ค้นหาข้อมูล:",
              zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
              paginate: {
                first: "หน้าแรก",
                last: "หน้าสุดท้าย",
                next: "ต่อไป",
                previous: "ย้อนกลับ"
              },
              aria: {
                sortAscending: ": activate to sort column ascending",
                sortDescending: ": activate to sort column descending"
              }
            }
          });
        });
      });
      await axios.get(`province/read`).then(response => {
        this.province_id = response.data;
      });
    },

    setCreditday() {
      if (this.editCustomer.payType == 1) {
        this.editCustomer.creditDay = 0;
        this.editCustomer.creditLimit = 0;
        this.editCustomer.priceType = 1;
      } else {
        this.editCustomer.creditDay = 30;
        this.editCustomer.creditLimit = 0;
        this.editCustomer.priceType = 3;
      }
    },

    importBill(val) {
      // sessionStorage.setItem("cust_id", val.cust_auto_id);
      // this.$router.push("/importWork");
      let imp_type;

      if (val.cust_auto_id == 1) {
        imp_type = 2;
      } else {
        imp_type = 1;
      }

      this.tmp_cust_name = val.customerName;
      this.tmp_cust_addr = val.customerAddr;
      this.tmp_cust_tel = val.customerTel;
      this.import_header.cust_auto_id = val.cust_auto_id;
      this.import_header.import_type_id = imp_type;
      this.showModal("importHeader");
    },

    async saveImportBill() {
      if (this.import_header.import_book_id == "" || this.import_header.import_book_no == "" || this.import_header.import_date == null) {
        alert("กรุณากรอกข้อมูลให้ครบถ้วน(เล่มที่, เลขที่ หรือ วันที่) !!!");
        document.getElementById("book_id").focus();
        document.getElementById("book_id").select();
      } else {
        await axios.post(`billimport/new`, this.import_header).then(() => {
          this.import_header = {
            import_book_id: "",
            import_book_no: "",
            cust_auto_id: "",
            import_user: "",
            import_type_id: "",
            import_date: new Date(),
            import_status: "รอดำเนินการ"
          };
          console.log("Process success!");
        }).catch(error => {
          console.log(error);
        });
      }
    },

    async showModal(e) {
      let myModal = await new Modal(document.getElementById(e));
      await myModal.show();
    },

    async hideModal(e) {
      await $(`#${e}`).removeClass("in");
      await $(".modal-backdrop").remove();
      await $(`#${e}`).hide();
    }

  }
};
