// ทำใบรับยางสำหรับการจ้างอัด
<template>
    <!-- เพิ่มรายละเอียดใบรับจากตารางราคา -->
    <div class="container-fluid" v-if="this.page == 2">
        <div class="card rounded-3 border-dark shadow" style="width: 100%">
            <div class="card-header bg-success opacity shadow" style="color: white; text-align:center;">
                ::: เพิ่มรายการใบรับยาง :::
            </div>
            <div class="card-body">
                <div class="row mt-1">
                    <div class="col-2 ms-2">
                        <div style="text-align:left;">
                            <label style="color:blue">หมายเลขยาง(จากโครง)</label>
                            <input type="text" class="form-control shadow" id="manufac_id"
                                v-model.trim="this.import_detail.manufac_id" placeholder="กรุณาใส่หมายเลขยาง"
                                @input="(this.import_detail.manufac_id = this.import_detail.manufac_id.toUpperCase())" />
                        </div>
                    </div>
                    <div class="col-3" style="text-align:left;">
                        <label style="color:blue">ยี่ห้อยาง</label>
                        <select class="form-select shadow" id="findBrand" v-model="this.import_detail.rub_brand_id">
                            <option v-for="rubberBrand in rubber_brand" :key="rubberBrand.rub_brand_id"
                                :value="rubberBrand.rub_brand_id">
                                {{ rubberBrand.rub_brand_shotname }} > {{ rubberBrand.rub_brand_name }}
                            </option>
                        </select>
                    </div>
                    <div class="col me-2" style="text-align:left;">
                        <label style="color:blue">รายละเอียดสินค้า</label>
                        <input type="text" class="form-control shadow" v-model="this.fixcost_detail"
                            placeholder="เลือกจากรายการด้านล่าง" readonly />
                    </div>
                </div>
                <!-- {{ this.import_detail }} -->
            </div>
        </div>
        <div class="card rounded-3 border-dark shadow-lg mt-1" style="width: 100%">
            <div class="card-header bg-success opacity-75 shadow" style="color: white; text-align:center;">
                ::: เลือกรายการใบรับจากตารางราคา :::
            </div>
            <div class="card-body">
                <table id="showproductcost" class="table table-striped table-hover table-sm" style="width: 100%;">
                    <thead>
                        <tr style="color:darkblue">
                            <th>ลำดับ</th>
                            <th>ประเภทการผลิต</th>
                            <th>รูปแบบการผลิต</th>
                            <th>ขนาด</th>
                            <th>ลายดอก</th>
                            <th>เกรดยาง</th>
                            <th>เงินสด</th>
                            <th>เงินสด+ส่ง</th>
                        </tr>
                    </thead>
                    <tbody class="table-group-divider">
                        <tr v-for="rec in product_cost" :key="rec.fixcost_auto_id"
                            @click.prevent="this.getFixcost(rec)">
                            <td>{{ product_cost.indexOf(rec) + 1 }}</td>
                            <td>{{ rec.pro_type_name }}</td>
                            <td>{{ rec.pro_detail }}</td>
                            <td>{{ rec.rub_size_id }}</td>
                            <td>{{ rec.rub_style_detail }}</td>
                            <td>{{ rec.rub_grade_name }}</td>
                            <td>{{ this.func.numFormat(rec.price1) }}</td>
                            <td>{{ this.func.numFormat(rec.price2) }}</td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <div class="card-footer" style="text-align:right">
                <!-- ปุ่มปิด -->
                <button type="button" class="btn btn-secondary ms-2 me-1" @click.prevent="clearSelect2();
                this.import_detail.rub_brand_id = ''; this.import_detail.manufac_id = '';
                this.fixcost_detail = ''; this.getImportDetail(this.import_header.import_id); this.page = 1">
                    <font-awesome-icon icon="fa-solid fa-ban" />&nbsp;ปิดหน้าต่าง
                </button>
                <!-- ปุ่มบันทึก -->
                <button id="btn" class="btn btn-primary ms-1" @click.prevent="saveImportDetail();">
                    <font-awesome-icon icon="fa-solid fa-circle-check" />&nbsp;บันทึก
                </button>
            </div>
        </div>
    </div>
    <!-- end เพิ่มรายละเอียดใบรับจากตารางราคา -->
    <!-- แสดง Header + รายละเอียดใบรับ -->
    <div class="container-fluid" v-if="this.page == 1">
        <div class="card rounded-3 shadow" style="width:100%;">
            <div class="card-header bg-primary" style="color:white; text-align:center;">
                ใบรับ (
                <select class="rounded-2 bg-primary border-0" style="color:yellow ;"
                    v-model="this.import_header.import_type_id" disabled>
                    <option value="1">ค่าจ้าง</option>
                    <option value="2">ยางพร้อมโครง</option>
                </select> )
            </div>
            <div class="card-body">
                <div class="row">
                    <div class="col" style="text-align:left;">
                        <label>เล่มที่</label>
                        <input type="text" class="form-control" id="book_id"
                            v-model.trim="this.import_header.import_book_id" readonly />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>เลขที่</label>
                        <input type="text" class="form-control" id="book_no"
                            v-model.trim="this.import_header.import_book_no" readonly />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>วันที่เปิดใบรับ(DD/MM/YYYY)</label>
                        <input type="text" class="date form-control" id="datepicker"
                            v-model="this.import_header.import_date" @click.prevent="showDatePicker();" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col" style="text-align:left;">
                        <label>ชื่อลูกค้า</label>
                        <input type="text" class="form-control" v-model="this.cust_name" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col-8" style="text-align:left;">
                        <label>ที่อยู่ลูกค้า</label>
                        <input type="text" class="form-control" v-model="this.cust_addr" readonly />
                    </div>
                    <div class="col-4" style="text-align:left;">
                        <label>เบอร์โทร</label>
                        <input type="text" class="form-control" v-model="this.cust_tel" readonly />
                    </div>
                </div>
                <div class="card-footer border-0" style="text-align:center">
                    <div class="row-12 mt-2 align-content-center">
                        <button class="btn btn-success rounded-2 me-2 btn-sm">
                            <font-awesome-icon icon="fa-solid fa-check-to-slot" />&nbsp;ยืนยันใบรับ
                        </button>
                        <button class="btn btn-warning rounded-2 me-2 btn-sm">
                            <font-awesome-icon icon="fa-solid fa-pen" />&nbsp;แก้ไขใบรับ
                        </button>
                        <button class="btn btn-danger rounded-2 me-2 btn-sm">
                            <font-awesome-icon icon="fa-solid fa-file-circle-xmark" />&nbsp;ยกเลิกใบรับ
                        </button>
                        <button class="btn btn-primary rounded-2 me-2 btn-sm" @click.prevent="this.getProductCost(this.import_header.import_type_id);
                        getUseData(); this.page = 2;">
                            <!-- newImportDetail() ตอนนี้ไม่ได้ใช้ -->
                            <font-awesome-icon icon="fa-solid fa-cart-plus" />&nbsp;เพิ่มรายการรับ
                        </button>
                    </div>
                </div>
            </div>
            <!-- <div class="card-footer align-content-center" id="control-detail"> -->

            <!-- </div> -->
        </div>
        <div class="card rounded-3 border-dark shadow mt-2" style="width: 100%">
            <div class="card-header bg-opacity-75 bg-primary" style="color:darkblue;text-align:center">
                ::: รายการใบรับยาง :::
            </div>
            <div class="card-body">
                <table id="showimportdetail" class="table table-striped table-hover table-sm nowrap"
                    style="width: 100%; align-items: center">
                    <thead>
                        <tr style="text-align:left; color:darkblue">
                            <th>ลำดับ</th>
                            <th>รหัสยาง</th>
                            <th>ประเภท/รูปแบบการผลิต</th>
                            <th>ขนาดยาง</th>
                            <th>ลายดอก</th>
                            <th>เกรดยาง</th>
                            <th>ยี่ห้อยาง</th>
                            <th>ราคาเงินสด/สด+ส่ง</th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody class="table-group-divider">
                        <tr v-for="rec in import_detail_list" :key="rec.import_detail_id" style="text-align:left">
                            <td>{{ import_detail_list.indexOf(rec) + 1 }}</td>
                            <td>{{ rec.manufac_id }}</td>
                            <td>{{ rec.pro_type_name }}/{{ rec.pro_detail }}</td>
                            <td>{{ rec.rub_size_id }}</td>
                            <td>{{ rec.rub_style_detail }}</td>
                            <td>{{ rec.rub_grade_name }}</td>
                            <td>{{ rec.rub_brand_shotname }}</td>
                            <td>{{ func.numFormat(rec.price1) }}/{{ func.numFormat(rec.price2) }}</td>
                            <td style="text-align:right">
                                <button id="edit" class="btn btn-primary btn-sm" title="แก้ไขรายการ">
                                    <font-awesome-icon icon="fa-solid fa-pen" style="color:white" />
                                </button>
                            </td>
                            <td style="text-align:left">
                                <button id="delete" class="btn btn-danger btn-sm" title="ลบรายการ"
                                    @click.prevent="removeImportDetail(rec.import_detail_id)">
                                    <font-awesome-icon icon="fa-solid fa-trash-can" style="color:white" />
                                </button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
    <!-- {{ this.import_header }} -->
</template>

<script>
import $ from "jquery"
import axios from "@/axios"
import { ref } from "vue"
import func from "../func.js"

export default {
    setup() {
        const date = ref(new Date());
        const format = (date) => {
            const d = date.getDate();
            const m = date.getMonth() + 1;
            const y = date.getFullYear();
            return `${d}/${m}/${y}`;
        }
        return {
            date, format
        }
    },
    components: {

    },
    data() {
        return {
            func: func,
            page: "1",
            cust_name: "",
            cust_addr: "",
            cust_tel: "",
            check_bill_header: [],
            customer_data: [],
            rubber_size: [],
            rubber_brand: [],
            product_cost: [],
            import_header: {
                import_id: "",
                import_book_id: "",
                import_book_no: "",
                import_user: "",
                cust_auto_id: "",
                import_type_id: "",
                import_date: "",
                import_status: "",
            },
            import_detail_list: [],
            fixcost_detail: "",
            import_detail: {
                import_id: "",
                rub_brand_id: "",
                manufac_id: "",
                fixcost_auto_id: "",
                import_detail_status: "",
            },
        }
    },
    mounted() {
        let import_book_id = sessionStorage.getItem("import_book_id");
        let import_book_no = sessionStorage.getItem("import_book_no");
        // alert(import_book_id+" "+import_book_no);
        if (import_book_id != null && import_book_no != null) {
            this.chkImportHeader(import_book_id, import_book_no);
        } else {
            let import_id = sessionStorage.getItem("import_id");
            this.import_detail.import_id = import_id;
            this.chkImportHeaderById(import_id);
            this.getImportDetail(import_id);
        }
    },
    beforeUnmount() {
        sessionStorage.removeItem("import_book_id");
        sessionStorage.removeItem("import_book_no");
        sessionStorage.removeItem("import_id");
    },
    methods: {
        showDatePicker() {
            let data = this.import_header;
            $(document).ready(() => {
                $('#datepicker').datepicker({
                    format: "dd/mm/yyyy",
                    autoclose: true,
                    toggleActive: true,
                });
                $('#datepicker').change(() => {
                    data.import_date = $('#datepicker').val();
                })
            });
        },
        convertDateToMySql(isdate) {
            const convertdate = isdate.split("/");
            let dateToMySql = convertdate[2] + "-" + convertdate[1] + "-" + convertdate[0];
            return dateToMySql;
        },
        onSelect_Brand() {
            $(document).ready(() => {
                let rec = this.import_detail;
                $('#findBrand').select2({
                    theme: "bootstrap-5",
                    closeOnSelect: true,
                    width: '100%',
                    dropdownParent: $("#findBrand").parent(),
                    placeholder: "--- กรุณาเลือกยี่ห้อยาง ---",
                });
                $('#findBrand').on('select2:select', (e) => {
                    let data = e.params.data;
                    rec.rub_brand_id = data.id;
                })
            });
        },
        clearSelect2() {
            $(document).ready(() => {
                $('#findBrand').val("");
            })
        },
        getUseData() {
            // this.getRubberSize();
            this.getRubberBrand();
            this.onSelect_Brand();
        },
        async chkImportHeader(val1, val2) {
            await axios.get(`billimport/search/${val1}/${val2}`).then((response) => {
                // this.check_bill_header = response.data;
                this.import_header.import_id = response.data[0].import_id;
                this.import_header.import_book_id = response.data[0].import_book_id;
                this.import_header.import_book_no = response.data[0].import_book_no;
                this.import_header.import_user = response.data[0].import_user;
                this.import_header.cust_auto_id = response.data[0].cust_auto_id;
                this.import_header.import_type_id = response.data[0].import_type_id;
                this.import_header.import_date = response.data[0].new_import_date;
                this.import_header.import_status = response.data[0].import_status;
                this.cust_name = response.data[0].customerName;
                this.cust_addr = response.data[0].customerAddr;
                this.cust_tel = response.data[0].customerTel;
                this.import_detail.import_id = response.data[0].import_id;
                this.getImportDetail(this.import_header.import_id);
            });
        },
        async chkImportHeaderById(val) {
            await axios.get(`billimport/searchid/${val}`).then((response) => {
                // this.check_bill_header = response.data;
                this.import_header.import_id = response.data[0].import_id;
                this.import_header.import_book_id = response.data[0].import_book_id;
                this.import_header.import_book_no = response.data[0].import_book_no;
                this.import_header.import_user = response.data[0].import_user;
                this.import_header.cust_auto_id = response.data[0].cust_auto_id;
                this.import_header.import_type_id = response.data[0].import_type_id;
                this.import_header.import_date = response.data[0].new_import_date;
                this.import_header.import_status = response.data[0].import_status;
                this.cust_name = response.data[0].customerName;
                this.cust_addr = response.data[0].customerAddr;
                this.cust_tel = response.data[0].customerTel;
            });
        },
        async newImportDetail() {
            // Load ตารางราคา
            await this.getProductCost();
            if ((this.import_header.import_book_id == null) ||
                (this.import_header.import_book_no == null) ||
                (this.import_header.import_date == null)) {
                alert('ข้อมูลหลักไม่ครบถ้วน.,กรุณาตรวจสอบ !!!')
                // document.getElementById("book_id").value = 'xxx';
                await document.getElementById("book_id").focus();
                await document.getElementById("book_id").select();
                //document.getElementById("book_no").disabled = true;             
            } else {
                await this.onSelect_Brand();
                await this.getUseData();
                // this.showModal('newImportDetail');
                await func.showModal('newImportDetail2');
            }
        },
        async saveImportDetail() {
            if (this.import_detail.manufac_id == "" ||
                this.import_detail.rub_brand_id == "" ||
                this.import_detail.fixcost_auto_id == "") {
                alert("กรุณากรอกข้อมูล หมายเลขยาง/ยี่ห้อยาง/รายการจากตารางราคา ให้ครบถ้วน!!!")
                func.setFocus("manufac_id");
            }
            else {
                if (await confirm("ยืนยันการบันทึกรายการ") == true) {
                    await axios.post(`billimport/detail/new`, this.import_detail).then(() => {
                        this.import_detail = {
                            import_id: this.import_header.import_id,
                            rub_brand_id: "",
                            manufac_id: "",
                            fixcost_auto_id: "",
                            import_detail_status: ""
                        };
                        this.fixcost_detail = "";
                    }).catch((error) => {
                        console.log(error);
                    });
                    // await func.hideModal("newImportDetail");
                    // alert(this.import_header.import_id);
                    // await $("#showimportdetail").DataTable().destroy();
                    this.page = 1;
                    await this.getImportDetail(this.import_header.import_id);
                }
            }
        },
        async getImportDetail(val) {
            await axios.get(`billimport/detail/read/${val}`).then((response) => {
                this.import_detail_list = response.data;
                $(document).ready(() => {
                    $("#showimportdetail").DataTable({
                        // dom: 'Bfrtip',
                        // buttons: [
                        //     'print',                            
                        // ],
                        "order": [[0, 'asc']],
                        "scrollY": '350px',
                        "scorllX": false,
                        "bJQueryUI": true,
                        "sPaginationType": "full_numbers",
                        "bFilter": false,
                        "paging": false,
                        "scrollCollapse": true,
                        "rowReorder": true,
                        destroy: true,
                        language: {
                            decimal: "",
                            emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
                            info: "พบข้อมูล _TOTAL_ เรคอร์ด",
                            // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
                            infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
                            infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
                            infoPostFix: "",
                            thousands: ",",
                            lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                            loadingRecords: "Loading...",
                            processing: "",
                            search: "ค้นหาข้อมูล:",
                            zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                            paginate: {
                                first: "หน้าแรก",
                                last: "หน้าสุดท้าย",
                                next: "ต่อไป",
                                previous: "ย้อนกลับ",
                            },
                            aria: {
                                sortAscending: ": activate to sort column ascending",
                                sortDescending: ": activate to sort column descending",
                            },
                        },
                    });
                });
            });
        },
        async removeImportDetail(rec) {
            if (await confirm("ยืนยันการลบรายการ ?") == true) {
                await axios.delete(`billimport/detail/delete/${rec}`).then(() => {
                    $("#showimportdetail").DataTable().destroy();
                    this.getImportDetail(this.import_header.import_id);
                }).catch((error) => {
                    alert('ไม่สามารถลบข้อมูลได้ ' + error);
                });
            }
        },
        getCustomer(val) {
            axios.get(`customer/findbyautoid/${val}`).then((response) => {
                // this.customer_data = response.data;
                // console.log(response.data[0].cust_auto_id);
                this.import_header = {
                    cust_auto_id: response.data[0].cust_auto_id
                }
                if (response.data[0].cust_auto_id == 1) { this.import_header.import_type_id = 2 }
                else this.import_header.import_type_id = 1

                this.cust_name = response.data[0].customerName
                this.cust_addr = response.data[0].customerAddr
                this.cust_tel = response.data[0].customerTel
            });
        },
        getRubberSize() {
            axios.get(`rubbersize/read`).then((response) => {
                this.rubber_size = response.data;
            });
        },
        getRubberBrand() {
            axios.get(`rubberbrand/read`).then((response) => {
                this.rubber_brand = response.data;
            });
        },
        getFixcost(rec) {
            this.import_detail.fixcost_auto_id = rec.fixcost_auto_id;

            this.fixcost_detail = rec.pro_type_name + " / " + rec.pro_detail + " / " +
                rec.rub_size_id + " / " + rec.rub_style_detail + " / " + rec.rub_grade_name + " / " +
                this.func.numFormat(rec.price1) + " / " + this.func.numFormat(rec.price2)
        },
        getProductCost(val) {
            axios.get(`productcost/readprotype/${val}`).then((response) => {
                this.product_cost = response.data;
                $(document).ready(() => {
                    $("#showproductcost").DataTable({
                        "order": [[0, 'asc']],
                        "scrollY": '250px',
                        "scorllX": false,
                        "bJQueryUI": true,
                        "sPaginationType": "full_numbers",
                        "bFilter": false,
                        "paging": false,
                        "scrollCollapse": true,
                        "rowReorder": true,
                        destroy: true,
                        language: {
                            decimal: "",
                            emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
                            info: "พบข้อมูล _TOTAL_ เรคอร์ด",
                            // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
                            infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
                            infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
                            infoPostFix: "",
                            thousands: ",",
                            lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                            loadingRecords: "Loading...",
                            processing: "",
                            search: "ค้นหาข้อมูล:",
                            zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                            paginate: {
                                first: "หน้าแรก",
                                last: "หน้าสุดท้าย",
                                next: "ต่อไป",
                                previous: "ย้อนกลับ",
                            },
                            aria: {
                                sortAscending: ": activate to sort column ascending",
                                sortDescending: ": activate to sort column descending",
                            },
                        },
                    });
                });
            });
        },
    },
}
</script>

