import $ from "jquery";
export default {
  data() {
    return {
      datetime: {
        getDate: ""
      }
    };
  },

  methods: {
    getDatepicker() {
      let select = this.datetime;
      $('#datepicker').datepicker({
        format: "dd/mm/yyyy",
        autoclose: true,
        toggleActive: true
      });
      $('#datepicker').change(() => {
        select.getDate = $('#datepicker').val();
      });
    }

  }
};
