// ทำใบรับยางสำหรับการจ้างอัด
<template>
    <!-- Modal เพิ่มรายละเอียดใบรับ -->
    <div class="modal fade rounded-3" id="newImportList" tabindex="-1">
        <div class="modal-dialog" role="document">
            <div class="modal-content">
                <div class="modal-header bg-primary opacity-75">
                    <h1 class="modal-title" style="color:white ;">
                        รายละเอียดใบรับ
                    </h1>
                </div>
            </div>
        </div>
    </div>
    <!-- End modal -->
    <div class="container" style="text-align:center;">
        <div class="card align-content-center rounded-3 shadow" style="max-width:auto;">
            <div class="card-header bg-primary" style="color:white; text-align:center;">
                ใบรับ
            </div>
            <div class="card-body">
                <div class="row">
                    <div class="col" style="text-align:left;">
                        <label>เล่มที่</label>
                        <input type="text" class="form-control" v-model="this.imp_header.imp_book_no" />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>เลขที่</label>
                        <input type="text" class="form-control" v-model="this.imp_header.imp_no" />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>วันที่เปิดใบรับ</label>
                        <!-- <input type="text" class="form-control" v-model="this.imp_header.imp_date" /> -->
                        <Datepicker v-model="this.imp_header.imp_date" :format="format" autoApply />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col" style="text-align:left;">
                        <label>ชื่อลูกค้า</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col-8" style="text-align:left;">
                        <label>ที่อยู่ลูกค้า</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                    <div class="col-4" style="text-align:left;">
                        <label>เบอร์โทร</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                </div>
            </div>
            <div class="card-footer align-content-center">
                <button class="btn btn-primary rounded-2 me-1">
                    <font-awesome-icon icon="fa-solid fa-cart-plus" />&nbsp;เพิ่มข้อมูล
                </button>
                <button class="btn btn-success rounded-2 ms-1">
                    <font-awesome-icon icon="fa-solid fa-check-to-slot" />&nbsp;ยืนยันใบรับ
                </button>
            </div>
        </div>
    </div>
    <!-- {{ this.imp_header }} -->
    <div class="container">
        <div class="card align-content-center rounded-3 shadow mt-2" style="max-width:auto;">
            <div class="card-header bg-primary" style="color:white ;text-align:center">รายละเอียดใบรับ</div>
            <div class="card-body">
            </div>
        </div>
    </div>
</template>

<script>
import $ from "jquery"
import { Modal } from "bootstrap"
import { ref } from "vue"

export default {
    setup() {
        const date = ref(new Date());
        const format = (date) => {
            const d = date.getDate();
            const m = date.getMonth() + 1;
            const y = date.getFullYear();
            return `${d}/${m}/${y}`;
        }
        return {
            date, format
        }
    },
    data() {
        return {
            showlist: false,
            imp_header: {
                imp_book_no: "",
                imp_no: "",
                imp_date: "",
            }
        }
    },
    mounted() {

    },
    methods: {
        getCurrentDate() {
            const currentDate = new Date();
            const dateNow = `${currentDate.getDate()}/${currentDate.getMonth() + 1}/${currentDate.getFullYear()}`
            return dateNow;
        },
        handleList() {
            this.showlist = !this.showlist
        },
        async showModal(e) {
            let myModal = await new Modal(document.getElementById(e))
            await myModal.show()
        },
        async hideModal(e) {
            await $(`#${e}`).removeClass("in");
            await $(".modal-backdrop").remove();
            await $(`#${e}`).hide();
        },
    },
}
</script>
