import $ from "jquery";
export default {
  name: "MyDatepicker",

  mounted() {
    this.getDatepicker();
  },

  methods: {
    getDatepicker() {
      $(document).ready(function () {
        $('#datepicker').on('click', () => {
          $('#datepicker').datepicker({
            format: "dd/mm/yyyy",
            autoclose: true,
            toggleActive: true
          });
        });
        $('#datepicker').change(() => {
          this.$emit("onSelect", $('#datepicker').val());
        });
      });
    }

  }
};
