import $ from "jquery";
export default {
  data() {
    return {
      datetime: {
        getDate: ""
      }
    };
  },

  methods: {
    getDatepicker() {
      // let select = this.datetime;
      $(document).ready(() => {
        $('#datepicker').datepicker({
          format: "dd/mm/yyyy",
          autoclose: true,
          toggleActive: true
        });
        $('#datepicker').change(() => {
          this.datetime.getDate = $('#datepicker').val();
        });
      });
    }

  }
};
