// ทำใบรับยางสำหรับการจ้างอัด
<template>
    <!-- Modal เพิ่มรายละเอียดใบรับ -->
    <div class="modal fade rounded-3" id="newImportList" tabindex="-1">
        <div class="modal-dialog modal-xl" role="document">
            <div class="modal-content">
                <div class="modal-header bg-primary opacity-75" style="color:white ;font-size:20px ;">
                    รายละเอียดใบรับ
                </div>
                <div class="modal-body">
                    <div class="row">
                        <div class="col-4" style="text-align:left;">
                            <label>ขนาดยาง</label>
                            <select class="form-select" id="findSize" v-model="this.imp_detail.rub_size_auto_id">
                                <option v-for="rubberSize in rubber_size" :key="rubberSize.id" :value="rubberSize.id">
                                    {{ rubberSize.rub_size_id }} > {{ rubberSize.rub_size_detail }}
                                </option>
                            </select>
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>ยี่ห้อยาง</label>
                            <select class="form-select" id="findBrand" v-model="this.imp_detail.rub_brand_id">
                                <option v-for="rubberBrand in rubber_brand" :key="rubberBrand.id" :value="rubberBrand.id">
                                    {{ rubberBrand.rub_brand_id }} > {{ rubberBrand.rub_brand_name }}
                                </option>
                            </select>
                        </div>
                        <div class="col-2" style="text-align:left;">
                            <label>หมายเลขยาง(จากโครง)</label>
                            <input type="text" class="form-control" />
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>หมายเหตุ</label>
                            <input type="text" class="form-control" />
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <!-- ปุ่มปิด -->
                    <button type="button" class="btn btn-secondary btn-lg" @click.prevent="clearSelect2();hideModal('newImportList')">
                        <font-awesome-icon icon="fa-solid fa-ban" />
                    </button>
                    <!-- ปุ่มบันทึก -->
                    <button id="btn" class="btn btn-primary btn-lg" @click.prevent="newImpDetail();">
                        <font-awesome-icon icon="fa-solid fa-circle-check" />
                    </button>
                </div>
            </div>
        </div>
    </div>
    <!-- End modal -->
    <div class="container" style="text-align:center;">
        <div class="card align-content-center rounded-3 shadow" style="max-width:auto;">
            <div class="card-header bg-primary" style="color:white; text-align:center;">
                ใบรับ
            </div>
            <div class="card-body">
                <div class="row">
                    <div class="col" style="text-align:left;">
                        <label>เล่มที่</label>
                        <input type="text" class="form-control" v-model="this.imp_header.imp_book_no" />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>เลขที่</label>
                        <input type="text" class="form-control" v-model="this.imp_header.imp_no" />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>วันที่เปิดใบรับ</label>
                        <!-- <input type="text" class="form-control" v-model="this.imp_header.imp_date" /> -->
                        <Datepicker v-model="this.imp_header.imp_date" :format="format" autoApply />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col" style="text-align:left;">
                        <label>ชื่อลูกค้า</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col-8" style="text-align:left;">
                        <label>ที่อยู่ลูกค้า</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                    <div class="col-4" style="text-align:left;">
                        <label>เบอร์โทร</label>
                        <input type="text" class="form-control" readonly />
                    </div>
                </div>
            </div>
            <div class="card-footer align-content-center">
                <button class="btn btn-primary rounded-2 me-1"
                    @click.prevent="onSelect2_Size(); getUseData(); showModal('newImportList')">
                    <font-awesome-icon icon="fa-solid fa-cart-plus" />&nbsp;เพิ่มข้อมูล
                </button>
                <button class="btn btn-success rounded-2 ms-1">
                    <font-awesome-icon icon="fa-solid fa-check-to-slot" />&nbsp;ยืนยันใบรับ
                </button>
                <button class="btn btn-danger rounded-2 ms-1">
                    <font-awesome-icon icon="fa-solid fa-file-circle-xmark" />&nbsp;ยกเลิกใบรับ
                </button>
            </div>
        </div>
    </div>
    <!-- {{ this.imp_header }} -->
    <div class="container">
        <div class="card align-content-center rounded-3 shadow mt-2" style="max-width:auto;">
            <div class="card-header bg-primary" style="color:white ;text-align:center">รายละเอียดใบรับ</div>
            <div class="card-body">
            </div>
        </div>
    </div>
</template>

<script>
import $ from "jquery"
import axios from "@/axios"
import { Modal } from "bootstrap"
import { ref } from "vue"

export default {
    setup() {
        const date = ref(new Date());
        const format = (date) => {
            const d = date.getDate();
            const m = date.getMonth() + 1;
            const y = date.getFullYear();
            return `${d}/${m}/${y}`;
        }
        return {
            date, format
        }
    },
    data() {
        return {
            rubber_size: [],
            rubber_brand: [],
            showlist: false,
            imp_header: {
                imp_book_no: "",
                imp_no: "",
                imp_date: "",
            },
            imp_detail: {
                rub_size_auto_id: "",
                rub_brand_id: "",
                rub_sn: "",
                note: "",
            },
        }
    },
    mounted() {

    },
    methods: {
        onSelect2_Size() {
            $(document).ready(() => {
                let rec = this.imp_detail;
                $('#findSize').select2({
                    theme: "bootstrap-5",
                    closeOnSelect: true,
                    width: '100%',
                    dropdownParent: $("#findSize").parent(),
                    placeholder: "--- เลือกขนาดยาง ---",
                });
                $('#findSize').on('select2:select', function (e) {
                    let data = e.params.data;
                    rec.rub_size_no = data.id;
                })
            });
        },
        clearSelect2() {
            $(document).ready(() => {
                $('#findSize').val("");
            })
        },
        newImpDetail() {
            if((confirm("ยืนยัน") == true)) {
                alert('ขอคิดก่อนนะ')
                this.hideModal("newImportList");
            }
        },
        handleList() {
            this.showlist = !this.showlist
        },
        getUseData() {
            this.getRubberSize();
            this.getRubberBrand();
        },
        getRubberSize() {
            axios.get(`rubbersize/read`).then((response) => {
                this.rubber_size = response.data;
            });
        },
        getRubberBrand() {
            axios.get(`rubberbrand/read`).then((response) => {
                this.rubber_brand = response.data;
            });
        },
        async showModal(e) {
            let myModal = await new Modal(document.getElementById(e))
            await myModal.show()
        },
        async hideModal(e) {
            await $(`#${e}`).removeClass("in");
            await $(".modal-backdrop").remove();
            await $(`#${e}`).hide();
        },
    },
}
</script>
