import $ from "jquery";
export default {
  name: "MyDatepicker",
  props: ["style", "xclass", "msg"],

  mounted() {
    this.getDatepicker();
  },

  methods: {
    getDatepicker() {
      $(document).ready(() => {
        $('#datepicker').datepicker({
          format: "dd/mm/yyyy",
          autoclose: true,
          toggleActive: true
        });
        $('#datepicker').change(() => {
          this.$emit("getDate", $('#datepicker').val());
        });
      });
    }

  }
};
