<template>
    <!-- Modal เพิ่มรายละเอียดใบรับ -->
    <div class="modal fade rounded-3" id="newImportList" tabindex="-1">
        <div class="modal-dialog modal-xl" role="document">
            <div class="modal-content">
                <div class="modal-header bg-primary opacity-75" style="color:white ;font-size:20px ;">
                    รายละเอียดใบรับ
                </div>
                <div class="modal-body">
                    <div class="row">
                        <div class="col-4" style="text-align:left;">
                            <label>ขนาดยาง</label>
                            <select class="form-select" id="findSize" v-model="this.import_detail.rub_size_auto_id">
                                <option v-for="rubberSize in rubber_size" :key="rubberSize.id" :value="rubberSize.id">
                                    {{ rubberSize.rub_size_id }} > {{ rubberSize.rub_size_detail }}
                                </option>
                            </select>
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>ยี่ห้อยาง</label>
                            <select class="form-select" id="findBrand" v-model="this.import_detail.rub_brand_id">
                                <option v-for="rubberBrand in rubber_brand" :key="rubberBrand.id"
                                    :value="rubberBrand.id">
                                    {{ rubberBrand.rub_brand_id }} > {{ rubberBrand.rub_brand_name }}
                                </option>
                            </select>
                        </div>
                        <div class="col-2" style="text-align:left;">
                            <label>หมายเลขยาง(จากโครง)</label>
                            <input type="text" class="form-control" />
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>หมายเหตุ</label>
                            <input type="text" class="form-control" />
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <!-- ปุ่มปิด -->
                    <button type="button" class="btn btn-secondary btn-lg"
                        @click.prevent="clearSelect2(); hideModal('newImportList')">
                        <font-awesome-icon icon="fa-solid fa-ban" />
                    </button>
                    <!-- ปุ่มบันทึก -->
                    <button id="btn" class="btn btn-primary btn-lg" @click.prevent="newImpDetail();">
                        <font-awesome-icon icon="fa-solid fa-circle-check" />
                    </button>
                </div>
            </div>
        </div>
    </div>
    <!-- End modal -->
    <div id="test">
        <div class="container" style="text-align:center;">
            <div class="card align-content-center rounded-3 shadow" style="max-width:auto;">
                <div class="card-header bg-primary" style="color:white; text-align:center; font-size: 20px;">
                    ใบรับ (
                    <select class="rounded-2 bg-primary border-0" style="color:yellow ;"
                        v-model="this.import_header.import_type_id" disabled>
                        <option value="1">ค่าจ้าง</option>
                        <option value="2">พร้อมโครง</option>
                    </select> )
                </div>
                <div class="card-body">
                    <div class="row">
                        <div class="col" style="text-align:left;">
                            <label>เล่มที่</label>
                            <input type="text" class="form-control" id="book_id"
                                v-model="this.import_header.import_book_id" />
                        </div>
                        <div class="col" style="text-align:left;">
                            <label>เลขที่</label>
                            <input type="text" class="form-control" id="book_no"
                                v-model="this.import_header.import_book_no" />
                        </div>
                        <div class="col" style="text-align:left;">
                            <label>วันที่เปิดใบรับ</label>
                            <input type="text" class="form-control" id="importdate"
                                v-model="this.import_header.import_date" />
                                
                            <div class="input-group date" id="datepicker">
                                <input type="text" class="form-control" id="date" />
                                <span class="input-group-append">
                                    <span class="input-group-text bg-light d-block">
                                        <i class="fa fa-calendar"></i>
                                    </span>
                                </span>
