import $ from "jquery";
export default {
  data() {
    return {
      getDate: ""
    };
  },

  methods: {
    getDatepicker() {
      let select = this;
      $('#datepicker').datepicker({
        format: "dd/mm/yyyy",
        autoclose: true,
        toggleActive: true
      });
      $('#datepicker_edit').change(() => {
        select.getDate = $('#datepicker_edit').val();
      });
    }

  }
