// แสดงรายการใบรับยาง
<template>
    <div class="card card-primary shadow-lg" style="width:100%">
        <div class="card-header bg-primary" style="color:white ;">
            ::: แสดงรายการ-ใบรับยาง :::
        </div>
        <div class="card-body">
            <table id="showbillimport" class="ms-2 table table-striped table-hover table-sm"
                style="width: 100%; align-items: center">
                <thead>
                    <tr>
                        <th>
                            <button class="btn btn-success btn-sm me-md-2">
                                <font-awesome-icon icon="fa-solid fa-plus" />
                            </button>
                        </th>
                        <th>เล่มที่</th>
                        <th>เลขที่</th>
                        <th>ลูกค้า</th>
                        <th>ประเภทใบรับ</th>
                        <th>วันที่ออกใบรับ</th>
                        <th>สถานะ</th>
                    </tr>
                </thead>
                <tbody class="table-group-divider">
                    <tr v-for="rec in get_import_header" :key="rec.import_id">
                        <td style="text-align: left">
                            <button id="edit" type="button" class="btn btn-dark btn-sm me-md-2" disabled
                                v-if="rec.import_status == 'เสร็จสิ้น'">
                                <font-awesome-icon icon="fa-solid fa-pen" />
                            </button>
                            <button id="edit" type="button" class="btn btn-primary btn-sm me-md-2" v-else>
                                <font-awesome-icon icon="fa-solid fa-pen" />
                            </button>
                            <button id="addbill" type="button" class="btn btn-primary btn-sm me-md-2">
                                <font-awesome-icon icon="fa-solid fa-file-circle-plus" />
                            </button>
                        </td>
                        <td>{{ rec.import_book_id }}</td>
                        <td>{{ rec.import_book_no }}</td>
                        <td>{{ rec.customerName }}</td>
                        <td>{{ rec.pro_type_name }}</td>
                        <td>{{ rec.new_import_date }}</td>
                        <!-- <td style="color:red" v-if="rec.import_status == 'รอดำเนินการ'">{{ rec.import_status }}</td>
                        <td style="color:green" v-else>{{ rec.import_status }}</td> -->
                        <td v-if="rec.import_status == 'รอดำเนินการ'">
                            <font-awesome-icon icon="fa-regular fa-lightbulb" style="color:red" />
                        </td>
                        <td v-else><font-awesome-icon icon="fa-regular fa-lightbulb" style="color:red" /></td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
</template>

<script>
import axios from "@/axios"
import $ from "jquery"
import { Modal } from "bootstrap"

export default {
    data() {
        return {
            get_import_header: [],
            import_header: {
                import_book_id: "",
                import_book_no: "",
                cust_auto_id: "",
                import_user: "",
                import_type_id: "",
                import_date: "",
                import_status: "",
            },
        }
    },
    mounted() {
        this.getImportHeader();
    },
    methods: {
        getImportHeader() {
            axios.get(`billimport/read`).then((response) => {
                this.get_import_header = response.data;
                this.setDatatables();
            });
        },
        setDatatables() {
            $(document).ready(function () {
                $("#showbillimport").DataTable({
                    "scrollY": '350px',
                    bJQueryUI: true,
                    sPaginationType: "full_numbers",
                    language: {
                        decimal: "",
                        emptyTable: "ไม่พบการข้อมูลจากฐานข้อมูล",
                        info: "พบข้อมูล _TOTAL_ เรคอร์ด",
                        // "info": "แสดง _START_ - _END_ แถว จากทั้งหมด _TOTAL_ แถว",
                        infoEmpty: "ไม่พบข้อมูลที่ค้นหา",
                        infoFiltered: "(จากข้อมูลทั้งหมด _MAX_ เรคอร์ด)",
                        infoPostFix: "",
                        thousands: ",",
                        lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                        loadingRecords: "Loading...",
                        processing: "",
                        search: "ค้นหาข้อมูล:",
                        zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                        paginate: {
                            first: "หน้าแรก",
                            last: "หน้าสุดท้าย",
                            next: "ต่อไป",
                            previous: "ย้อนกลับ",
                        },
                        aria: {
                            sortAscending: ": activate to sort column ascending",
                            sortDescending: ": activate to sort column descending",
                        },
                    },
                });
            });
        },
        async showModal(e) {
            let myModal = await new Modal(document.getElementById(e));
            await myModal.show();
        },
        async hideModal(e) {
            await $(`#${e}`).removeClass("in");
            await $(".modal-backdrop").remove();
            await $(`#${e}`).hide();
        },
    },
}
</script>
