// ทำใบรับยางสำหรับการจ้างอัด
<template>
    <!-- Modal เพิ่มรายละเอียดใบรับ -->
    <div class="modal fade rounded-3" id="newImportDetail" tabindex="-1">
        <div class="modal-dialog modal-xl" role="document">
            <div class="modal-content">
                <div class="modal-header bg-primary opacity-75" style="color:white ;font-size:20px ;">
                    รายละเอียดใบรับ
                </div>
                <div class="modal-body">
                    <div class="row">
                        <div class="col-4" style="text-align:left;">
                            <label>ขนาดยาง</label>
                            <select class="form-select" id="findSize" v-model="this.import_detail.rub_size_id">
                                <option v-for="rubberSize in rubber_size" :key="rubberSize.id" :value="rubberSize.id">
                                    {{ rubberSize.rub_size_id }} > {{ rubberSize.rub_size_detail }}
                                </option>
                            </select>
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>ยี่ห้อยาง</label>
                            <select class="form-select" id="findBrand" v-model="this.import_detail.rub_brand_id">
                                <option v-for="rubberBrand in rubber_brand" :key="rubberBrand.rub_brand_id"
                                    :value="rubberBrand.rub_brand_id">
                                    {{ rubberBrand.rub_brand_shotname }} > {{ rubberBrand.rub_brand_name }}
                                </option>
                            </select>
                        </div>
                        <div class="col-2" style="text-align:left;">
                            <label>หมายเลขยาง(จากโครง)</label>
                            <input type="text" class="form-control" v-model="this.import_detail.manufac_id"
                                @input="(this.import_detail.manufac_id = this.import_detail.manufac_id.toUpperCase())" />
                        </div>
                        <div class="col-3" style="text-align:left;">
                            <label>หมายเหตุ</label>
                            <input type="text" class="form-control" v-model="this.import_detail.import_detail_status" />
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <!-- ปุ่มปิด -->
                    <button type="button" class="btn btn-secondary"
                        @click.prevent="clearSelect2(); this.func.hideModal('newImportDetail') ">
                        <font-awesome-icon icon="fa-solid fa-ban" />&nbsp;ปิดหน้าต่าง
                    </button>
                    <!-- ปุ่มบันทึก -->
                    <button id="btn" class="btn btn-primary" @click.prevent="saveImportDetail();">
                        <font-awesome-icon icon="fa-solid fa-circle-check" />&nbsp;บันทึก
                    </button>
                </div>
                <!-- {{ this.import_detail }} -->
            </div>
        </div>
    </div>
    <!-- End modal -->
    <div class="container" style="text-align:center;">
        <div class="card align-content-center rounded-3 shadow" style="max-width:auto;">
            <div class="card-header bg-primary" style="color:white; text-align:center; font-size: 20px;">
                ใบรับ (
                <select class="rounded-2 bg-primary border-0" style="color:yellow ;"
                    v-model="this.import_header.import_type_id" disabled>
                    <option value="1">ค่าจ้าง</option>
                    <option value="2">พร้อมโครง</option>
                </select> )
                <button class="btn btn-success rounded-2 me-2">
                    <font-awesome-icon icon="fa-solid fa-check-to-slot" />&nbsp;ยืนยันใบรับ
                </button>
            </div>
            <div class="card-body">
                <div class="row">
                    <div class="col" style="text-align:left;">
                        <label>เล่มที่</label>
                        <input type="text" class="form-control" id="book_id" v-model="this.import_header.import_book_id"
                            readonly />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>เลขที่</label>
                        <input type="text" class="form-control" id="book_no" v-model="this.import_header.import_book_no"
                            readonly />
                    </div>
                    <div class="col" style="text-align:left;">
                        <label>วันที่เปิดใบรับ(DD/MM/YYYY)</label>
                        <input type="text" class="date form-control" id="datepicker"
                            v-model="this.import_header.import_date" @click.prevent="showDatePicker();" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col" style="text-align:left;">
                        <label>ชื่อลูกค้า</label>
                        <input type="text" class="form-control" v-model="this.cust_name" readonly />
                    </div>
                </div>
                <div class="row mt-1">
                    <div class="col-8" style="text-align:left;">
                        <label>ที่อยู่ลูกค้า</label>
                        <input type="text" class="form-control" v-model="this.cust_addr" readonly />
                    </div>
                    <div class="col-4" style="text-align:left;">
                        <label>เบอร์โทร</label>
                        <input type="text" class="form-control" v-model="this.cust_tel" readonly />
                    </div>
                </div>
                <div class="row-12 mt-2 align-content-center">
                    <button class="btn btn-warning rounded-2 me-2">
                        <font-awesome-icon icon="fa-solid fa-pen" />&nbsp;แก้ไขใบรับ
                    </button>
                    <button class="btn btn-danger rounded-2 me-2">
                        <font-awesome-icon icon="fa-solid fa-file-circle-xmark" />&nbsp;ยกเลิกใบรับ
                    </button>
                    <button class="btn btn-primary rounded-2 me-2" @click.prevent="newImportDetail()">
                        <font-awesome-icon icon="fa-solid fa-cart-plus" />&nbsp;เพิ่มรายการรับ
                    </button>
                </div>
            </div>
            <div class="card-footer align-content-center" id="control-detail">
                <div class="card border-dark shadow-lg mt-2" style="width: 100%">
                    <div class="card-header bg-opacity-50 bg-info" style="color:darkblue;text-align:center">
                        ::: รายการใบรับยาง :::
                    </div>
                    <div class="card-body">
                        <table id="showimportdetail" class="ms-2 table table-striped table-hover table-sm nowrap"
                            style="width: 100%; align-items: center">
                            <thead>
                                <tr style="text-align:left">
                                    <th>ลำดับ</th>
                                    <th>ขนาดยาง</th>
                                    <th>ยี่ห้อยาง</th>
                                    <th>รหัสยาง</th>
                                    <th>เพิ่มเติม</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody class="table-group-divider">
                                <tr v-for="rec in import_detail_list" :key="rec.import_detail_id" style="text-align:left">
                                    <td>{{ import_detail_list.indexOf(rec) + 1 }}</td>
                                    <td>{{ rec.rub_size_id }}</td>
                                    <td>{{ rec.rub_brand_name }}</td>
                                    <td>{{ rec.manufac_id }}</td>
                                    <td>{{ rec.import_detail_status }}</td>
                                    <td>
                                        <button id="edit" class="btn btn-primary btn-sm" title="แก้ไขข้อมูล"
                                            @click.prevent="">
                                            <font-awesome-icon icon="fa-solid fa-pen" />
                                        </button>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    </div>
    <!-- {{ this.import_header }} -->

    <!-- รายการในใบรับ -->
    <!-- <div class="container"> -->

    <!-- </div> -->
</template>

