                            thousands: ",",
                            lengthMenu: "แสดงครั้งละ _MENU_ เรคอร์ด",
                            loadingRecords: "Loading...",
                            processing: "",
                            search: "ค้นหาข้อมูล:",
                            zeroRecords: "ไม่พบข้อมูลที่ค้นหา",
                            paginate: {
                                first: "หน้าแรก",
                                last: "หน้าสุดท้าย",
