import $ from "jquery"
import { Modal } from "bootstrap"
export default {
    async showModal(e) {
        let myModal = await new Modal(document.getElementById(e));
        await myModal.show();
    },
    async hideModal(e) {
        const body = document.querySelector("body");
        await $(".modal-backdrop").remove();
        await $(`#${e}`).removeClass("in");
        await $(`#${e}`).hide();
        body.style.overflow = "auto";
    },
    setFocus(input_id) {
        let event = document.querySelector(`#${input_id}`);
        event.focus();
        event.addEventListener("focus", () => {
            event.select();
        })
    },
    setFocusInModal(Modal, InputModal) {
        const myModal = document.getElementById(Modal);
        const myInput = document.getElementById(InputModal);
        myModal.addEventListener("shown.bs.modal", () => {
            myInput.focus();
        });
    },
    numFormat(e) {
        const nf = new Intl.NumberFormat("th-TH", {
            style: "currency",
            currency: "THB",
            maximumFractionDigits: 2,
        }).format(e);
        return nf;
    },
    //    กำหนดให้พิมพ์ได้แค่ภาษาอังกฤษ และตัวเลข
    fixCharecter(evt) {
        let char = String.fromCharCode(evt.keyCode);
        if (/^[A-Za-z0-9]+$/.test(char)) return true;
        else evt.preventDefault();
        return char;
    },
    showDatePicker(textdate) {
        $(`#${textdate}`).datepicker({
            format: "dd/mm/yyyy",
            autoclose: true,
            toggleActive: true,
        });
        $(`#${textdate}`).change(() => {
            return $(`#${textdate}`).val();
        });
    },
}
